import React, { useEffect, useState} from "react";
import Styles from "./UI/styles.module.css";
import { ToDo } from "../../entities/ToDo/ToDo";

export type ToDoId = {
	id: number
}

export type ToDo = ToDoId & {
	title: string,
	description: string,
	isDone: boolean,
	todoListId: number,
	createdAt: string,
	updatedAt: string
}

export type ToDoProps = ToDo & {
	funcDelete: (id: number) => void
}

async function fetchToDoList() {
	try {
		const token = localStorage.getItem("token");
		const res = await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "GET",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			}
		})
		if (res.ok) {
			const data = await res.json();
			return data;
		}
	} catch(error) {
		console.log(error);
	}
}

async function fetchDeleteToDo(id: ToDoId) {
	try {
		const token = localStorage.getItem("token");
		await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "DELETE",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			},
			body: JSON.stringify({
				id
			})
		})
	} catch(error) {
		console.log(error);
	}
}

export function ToDoList() {
	const [toDoList, setToDoList] = useState<ToDo[]>([]);

	function deleteToDo(id: ToDoId): void {
		setToDoList(preToDoList => preToDoList.filter(item => item.id !== id));
		fetchDeleteToDo(id);
	}

	useEffect(() => {
		async function getToDoList() {
			const result = await fetchToDoList();
			setToDoList(result.reverse());
		}
		getToDoList();
	}, [])
	console.log(toDoList[0])
	return (
		<div className={Styles.container}>
		 {toDoList.length === 0 ? <div className={Styles.flexContainer}><h2 className={Styles.noToDoH2}>{"Твой ТуДушка пуст :("}</h2></div> : (
        toDoList.map((item, index, arr) => (
          <ToDo funcDelete={deleteToDo} {...item}/>
        ))
      )}
		</div>
	);
}
