import React, { useEffect, useReducer, useState} from "react";
import Styles from "./UI/styles.module.css";
import { ToDoComponent } from "../../entities/ToDoComponent/ToDoComponent";
import { AddToDoAction, ToDo, ToDoId, store } from "../../app/store";

export type ToDoProps = ToDo & {
	funcDelete: (id: number) => void,
	idInList: number
}

async function fetchToDoList():Promise<ToDo[] | undefined> {
	try {
		const token = localStorage.getItem("token");
		const res = await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "GET",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			}
		})
		if (res.ok) {
			const data = await res.json();
			localStorage.setItem('todoListId', data.todoListId);
			return data.toDos as ToDo[];
		}
	} catch(error) {
		console.log(error);
		return [];
	}
}

async function fetchDeleteToDo(id: ToDoId):Promise<void> {
	try {
		const token = localStorage.getItem("token");
		await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "DELETE",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			},
			body: JSON.stringify({
				id
			})
		})
	} catch(error) {
		console.log(error);
	}
}

export function ToDoList() {
	const [, setToDoList] = useState<ToDo[]>([]);

	const [,forceUpdate] = useReducer(x => x + 1, 0);
	useEffect(() => {
		const unsubscribe = store.subscribe(() => {
			forceUpdate();
		});
		return unsubscribe;
	})

	function deleteToDo(id: ToDoId): void {
		setToDoList(preToDoList => preToDoList.filter(item => item.id !== id));
		fetchDeleteToDo(id);
	}

	useEffect(() => {
		async function getToDoList():Promise<void> {
			const result: ToDo[] | undefined = await fetchToDoList();
			if (result) {
				store.dispatch({type: 'addToDo', payload: result} satisfies AddToDoAction)
			}
		}
		getToDoList();
	}, [])
	const toDoList = store.getState().toDos;
	return (
		<div className={Styles.container}>
		 {store.getState().toDos.length === 0 ? <div className={Styles.flexContainer}><h2 className={Styles.noToDoH2}>{"Твой ТуДушка пуст :("}</h2></div> : (
        toDoList.reverse().map((item: ToDo, index: number, arr: ToDo[]) => (
          <ToDoComponent funcDelete={deleteToDo} key={item.id} {...item} idInList={arr.length - index} />
        ))
      )}
		</div>
	);
}
