import React, { useRef, useState, useEffect, useReducer } from "react";
import Styles from "./UI/styles.module.css";
import { IFormAddToDo } from "../../app/models/models";
import { Button } from "../../shared/Button/Button";
import { store, ToDoDescription, ToDoTitle, AddToDoAction, ToDoDateComplete } from "../../app/store";
import { isDisabled } from "@testing-library/user-event/dist/utils";

async function fetchCreateToDo(title:ToDoTitle, dateCompleted: ToDoDateComplete, description:ToDoDescription) {
	try {
		const token = localStorage.getItem('token');
		const res = await fetch("http://localhost:6001/api/user/to-do-list/", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
				"authorization": "Bearer " + token
			},
			body: JSON.stringify({
				title,
				description,
				dateCompleted,
			})
		})
		if (res.ok) {
			const data = await res.json().then(data => data.idToDo);
			localStorage.setItem('idToDo', data)
		}
	} catch(error) {
		console.log(error);
	}
}

export function FormAddToDoWindow(props:IFormAddToDo) {
	const [,forceUpdate] = useReducer(x => x + 1, 0);

	useEffect(() => {
		const unsubscribe = store.subscribe(() => {
				forceUpdate();
		});
		return unsubscribe;
	}, [])

	const titleRef = useRef<HTMLInputElement>(null);
	const descriptionRef = useRef<HTMLTextAreaElement>(null);
	const [errorText, setErrorText] = useState<string>('');
	const [dateCompleted, setdateCompleted] = useState<Date>(() => {
		const today = new Date()
		const tomorrow = new Date(today);
		tomorrow.setDate(tomorrow.getDate() + 1);
		tomorrow.setHours(3,0,0,0);
		return tomorrow;
	});
	const [isChoiceDate, setIsChoiceDate] = useState<boolean>(false);
	const [dateInput, setDateInput] = useState<string>();
	const [isDisabledInputDay, setIsDisabledInputDay] = useState<boolean>(true);
	useEffect(() => {
		if (dateInput) {
			const dateString:string = dateInput;
			const date:number[] = dateString.split('-').map(item => parseInt(item));
			let month:number = date[1] - 1;
			let year:number = date[0];
			let day:number = date[2];

			const today:Date = new Date();
			if(today.getFullYear() <= year && today.getMonth() <= month && today.getDate() < day) {
				setIsDisabledInputDay(true);
			}
			setIsDisabledInputDay(false);
		}
	setIsDisabledInputDay(true);
	}, [dateInput])
	
	function handlerSubmit(e: React.FormEvent<HTMLFormElement>) {
		e.preventDefault();
		async function addToDo() {
			const title = titleRef.current?.value;
			const desc = descriptionRef.current?.value;
			const description = desc ? desc : ' ';
			const dateComplete = JSON.stringify(dateCompleted);
			if (title && description) {
				await fetchCreateToDo(title, dateComplete, description);
				store.dispatch({type: 'addToDo', payload: [{title, description, dateComplete, id: Number(localStorage.getItem('idToDo')), todoListId: Number(localStorage.getItem('toDoListId')), isDone: false}]} satisfies AddToDoAction)
				props.funcExitWindow();
			} else {
				setErrorText("Введите заголовок")
			}
		}
		addToDo();
	}
	function handlerChoiceDate(e: React.MouseEvent<HTMLButtonElement>): void {
		e.preventDefault();
		setIsChoiceDate(true);
	}
	function minDate():string {
		const date = new Date();
		let month = `${date.getMonth() + 1}`;
		let day = `${date.getDate()}`;
		let year = `${date.getFullYear()}`

		if (date.getMonth() < 10) {
			month = '0' + month;
		}
		if (date.getDate() < 10) {
			day = '0' + day;
		}
		return `${year}-${month}-${day}`
	}

	function minTime():string {
		if (dateInput) {
			const dateString:string = dateInput;
			const date:number[] = dateString.split('-').map(item => parseInt(item));
			let month:number = date[1] - 1;
			let year:number = date[0];
			let day:number = date[2];

			const today:Date = new Date();
			let minutes:string = today.getMinutes() + 1 +'';
			let hours:string = today.getHours() + '';
			if (minutes.length < 2) {
				minutes = '0' + minutes;
			}
			if(hours.length < 2) {
				hours = '0' + hours;
			}
			if (year === today.getFullYear() && month === today.getMonth() && day === today.getDate()) {
				return `${hours}:${minutes}`;
			}
		}
		return '00:00'
	}
	console.log(dateInput)

	function handlerFormDateSubmit(e: React.FormEvent<HTMLFormElement>):void {
		e.preventDefault();
		setIsChoiceDate(false);
	}

	return (
		<div className={Styles.container}>
			<form onSubmit={handlerSubmit} className={Styles.form} >
				<h2 className={Styles.formAddToDoWindowH2}>Новая задача</h2>
				<svg onClick={() => props.funcExitWindow()} role="button" aria-label="Кнопка закрыть форму авторизации" className={Styles.closeAddToDoForm} viewBox="0 0 12 12">
					<line x1="1" y1="11" x2="11" y2="1" stroke="#FF4D00" strokeWidth="1" />
					<line x1="1" y1="1" x2="11" y2="11" stroke="#FF4D00" strokeWidth="1" />
				</svg>
				<div className={Styles.inputContainer}>
					<label>Введите заголовок: </label>
					<input ref={titleRef} placeholder="Заголовок" autoComplete="false" type="text" className={Styles.formAddToDoInput}/>
				</div>
				<div className={Styles.inputContainer}>
					<label>Введите примечание: </label>
					<textarea ref={descriptionRef} autoComplete="false" placeholder="Примечание (необязательное поле)" className={Styles.formAddToDoInput} />
				</div>
				<div className={Styles.buttonsContainer}>
					<button className={Styles.buttonChoiceDate} type="button" onClick={handlerChoiceDate}>Укажите дату выполнения (по умолчанию – до конца дня)</button>
					<Button type={1}>Создать</Button>
				</div>
				<p className={Styles.errorText}>{errorText}</p>
			</form>
			{isChoiceDate ? <div  className={Styles.formDateContainer}>
				<form  className={Styles.formDate} onSubmit={handlerFormDateSubmit}>
				<div className={Styles.inputContainer}>
					<label htmlFor="input3">Выберите дату: </label>
					<input min={minDate()} id="input3" type="date" className={Styles.formAddToDoInput} value={dateInput} onChange={(e) => setDateInput(e.target.value)}/>
				</div>
				<div className={Styles.inputContainer}>
					<label htmlFor="input3">Выберите время (необязатеьное поле): </label>
					<input min={minTime()} id="input3" type="time" className={Styles.formAddToDoInput} disabled={isDisabledInputDay}/>
				</div>
					<Button type={1}>Ок</Button>
				</form>
			</div>: null}
		</div>
	);
}
