import React, { useEffect, useState } from "react";
import { Home } from "./pages/Home/Home";
import { Route, Routes, Navigate } from "react-router-dom";
import { LoadingPage } from "./pages/LoadingPage/LoadingPage";
import { ErrorServer } from "./pages/ErrorServer/ErrorServer";

async function checkAuthorization():Promise<boolean | null> {

	const controller = new AbortController();
	const timeoutId = setTimeout(():null => {
		controller.abort();
		return null
	}, 5000);

	try {
		const token = localStorage.getItem('token');
		const res = await fetch('http://localhost:6001/api/user/check', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'authorization': 'Bearer ' + token
			},
			signal: controller.signal
		})

		clearTimeout(timeoutId);

		if(!res.ok) {
			console.error('Статус ошибки: ', res.status, '\nСообщение ошибки: ', res.statusText);
			return false;
		}
		const data:string = await res.json();
		localStorage.setItem('token', data)
		return true;
	} catch(error: unknown) {
		console.error("Ошибка с сервером: ", error);
		if (error.name === 'AbortError') {
			console.error('Превышено время ожидания ответа от сервера. ', error);
			return null;
		}
		return null;
	}
}
export default function App() {
	const [isChecked, setIsChecked] = useState<boolean | null | undefined>(undefined);

	useEffect(() => {
		const authorize = async () => {
			const result = await checkAuthorization();
			setIsChecked(result);
		}
		authorize();
	}, []);

  return (
    <div className="App">
      <Routes>
				<Route path = "/" element={isChecked === undefined ? <LoadingPage/> : isChecked === null ? <Navigate to="/error/500" replace/> : <Navigate to="/home" replace/>}/>
	
        <Route path="/home" element={isChecked !== undefined && isChecked !== null ? <Home isChecked={isChecked} /> : <Navigate to = "/" replace/>} />

        <Route path="/error/500" element={isChecked === null && isChecked === undefined ? <ErrorServer /> : <Navigate to ="/" replace/>} />
      </Routes>
    </div>
  );
}
