import React, { useEffect, useState } from "react";
import { Home } from "./pages/Home/Home";
import { Route, Routes, Navigate } from "react-router-dom";
import { LoadingPage } from "./pages/LoadingPage/LoadingPage";
import { ErrorServer } from "./pages/ErrorServer/ErrorServer";

async function checkAuthorization():Promise<boolean | null> {

	const controller = new AbortController();
	const timeoutId = setTimeout(():null => {
		controller.abort();
		return null
	}, 5000);

	try {
		const token = localStorage.getItem('token');
		if (!token) {
			return false;
		}
		const res = await fetch('http://localhost:6001/api/user/check', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'authorization': 'Bearer ' + token
			},
			signal: controller.signal
		})

		clearTimeout(timeoutId);

		if(!res.ok) {
			console.error('Статус ошибки: ', res.status, '\nСообщение ошибки: ', res.statusText);
			return false;
		}
		const data = await res.json();
		localStorage.setItem('token', data)
		return true;
	} catch(error: any) {
		console.error("Ошибка с сервером: ", error);
		if (error.name === 'AbortError') {
			console.error('Превышено время ожидания ответа от сервера. ', error);
			return null;
		}
		return null;
	}
}
export default function App() {
	const [isChecked, setIsChecked] = useState<boolean | null>(null);
	const [renderElement, setRenderElement] = useState<React.ReactNode>(null)

	//Запуск функции на проверку валидации
	useEffect(() => {
		const authorize = async () => {
			const result = await checkAuthorization();
			setIsChecked(result);
			console.log(result)
			console.log(isChecked)
			// if(!result) {
			// 	setRenderElement(<Navigate to="/error/500" replace/>)
			// } else {
			// 	setRenderElement(<Navigate to="/home" replace/>)
			// }
		}
		authorize();
	}, []);

	return (
    <div className="App">
      <Routes>
				<Route path="/" element = {<LoadingPage/>}/>
        <Route path="/home" element = { <Home isChecked/>}/>
				<Route path="/error/500" element = { <ErrorServer/> }/>
      </Routes>
			{renderElement}
    </div>
  );
}
