import React, { useEffect, useState } from "react";
import { Home } from "./pages/Home/Home";
import { ErrorServer } from "./pages/ErrorServer/ErrorServer";

async function checkAuthorization():Promise<boolean> {

	const token = localStorage.getItem('token');

	if (!token) {
		return false;
	}

	try {
		const controller = new AbortController();
		 const timeoutId = setTimeout(() => {
			controller.abort();
		}, 5000);

		const res = await fetch('http://localhost:6001/api/user/check', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'authorization': 'Bearer ' + token
			},
			signal: controller.signal
		})

		clearTimeout(timeoutId);

		if(!res.ok) {
			console.error('Статус ошибки: ', res.status, '\nСообщение ошибки: ', res.statusText);
			return false;
		}
		const data = await res.json();
		localStorage.setItem('token', data)
		return true;
	} catch(error: any) {
		console.error("Ошибка с сервером: ", error);
		if (error.name === 'AbortError') {
			console.error('Превышено время ожидания ответа от сервера. ', error);
			return false;
		}
		return false;
	}
}

export default function App() {
	const [isChecked, setIsChecked] = useState<boolean | null>(null);
	useEffect(() => {
		const authorize = async () => {
			const result = await checkAuthorization();
			setIsChecked(result);
		}
		authorize();
	}, []);

	return (
		<div className="App">
			{isChecked === null ? <div>Загрузка...</div> : <Home />}
		</div>
	);
}
