import React, { useRef, useState, useEffect, useReducer } from "react";
import Styles from "./UI/styles.module.css";
import { IFormAddToDo } from "../../app/models/models";
import { Button } from "../../shared/Button/Button";
import { store, ToDoDescription, ToDoTitle, AppState, ToDoState, AddToDoAction } from "../../app/store";

async function fetchCreateToDo(title:ToDoTitle, description:ToDoDescription) {
	try {
		const token = localStorage.getItem('token');
		const res = await fetch("http://localhost:6001/api/user/to-do-list/", {
			method: "POST",
			headers: {
				"Content-Type": "application/json",
				"authorization": "Bearer " + token
			},
			body: JSON.stringify({
				title,
				description
			})
		})
		if (res.ok) {
			const data = await res.json()
			localStorage.setItem('idToDo', data.idToDo)
			return true
		}
		return false;
	} catch(error) {
		console.log(error);
		return false;
	}
}

export function FormAddToDoWindow(props:IFormAddToDo) {
	const [,forceUpdate] = useReducer(x => x + 1, 0);

	useEffect(() => {
		const unsubscribe = store.subscribe(() => {
				forceUpdate();
		});
		return unsubscribe;
	}, [])

	const titleRef = useRef<HTMLInputElement>(null);
	const descriptionRef = useRef<HTMLTextAreaElement>(null);
	const [errorText, setErrorText] = useState<string>('');

	function handlerSubmit(e: React.FormEvent<HTMLFormElement>) {
		e.preventDefault();
		async function addToDo() {
			const title = titleRef.current?.value;
			const description = descriptionRef.current?.value;
			if (title && description) {
				const result:boolean = await fetchCreateToDo(title, description);
				if (result) {
					const idToDo = parseInt(localStorage.getItem('idToDo')); 
					// store.dispatch({type: 'addToDo', payload: [{title, description, id: localStorage.getItem('idToDo'), todoListId: localStorage.getItem('toDoListId'), isDone: false}]} satisfies AddToDoAction)
					props.funcExitWindow();
				}
			} else {
				setErrorText('Что-то пошло не так :(')
			}
		}
		addToDo();
	}

	return (
		<div className={Styles.container}>
			<form onSubmit={handlerSubmit} className={Styles.form} >
				<h2 className={Styles.formAddToDoWindowH2}>Новая задача</h2>
				<svg onClick={() => props.funcExitWindow()} role="button" aria-label="Кнопка закрыть форму авторизации" className={Styles.closeAddToDoForm} viewBox="0 0 12 12">
					<line x1="1" y1="11" x2="11" y2="1" stroke="#FF4D00" strokeWidth="1" />
					<line x1="1" y1="1" x2="11" y2="11" stroke="#FF4D00" strokeWidth="1" />
				</svg>
				<div className={Styles.inputContainer}>
					<label>Введите заголовок: </label>
					<input ref={titleRef} placeholder="Заголовок" autoComplete="false" type="text" className={Styles.formAddToDoInput}/>
				</div>
				<div className={Styles.inputContainer}>
					<label>Введите примечание: </label>
					<textarea ref={descriptionRef} autoComplete="false" placeholder="Примечание (необязательное поле)" className={Styles.formAddToDoInput}/>
				</div>
				<Button type={1}>Создать</Button>
				<p className={Styles.errorText}>{errorText}</p>
			</form>
		</div>
	);
}
