import React, { useEffect, useRef} from "react";
import Styles from "./UI/styles.module.css";
import { ToDoComponent } from "../../entities/ToDoComponent/ToDoComponent";
import { LoadToDosAction, DeleteToDoAction, ToDo, ToDoId, store, ToDoDateComplete, AppState } from "../../app/store";
import { getMonth } from "../../features/FormChoiceDate/getMonth";
import { useSelector } from "react-redux";

export type ToDoProps = ToDo & {
	funcDelete: (id: ToDoId, taskDateComplete: ToDoDateComplete) => void,
	idInList: number
}

async function fetchToDoList():Promise<ToDo[] | undefined> {
	try {
		const token = localStorage.getItem("token");
		const res = await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "GET",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			}
		})
		if (res.ok) {
			const data = await res.json();
			localStorage.setItem('todoListId', data.todoListId);
			return data.toDos as ToDo[];
		}
	} catch(error) {
		console.log(error);
		return [];
	}
}

async function fetchDeleteToDo(id: ToDoId):Promise<void> {
	try {
		const token = localStorage.getItem("token");
		await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "DELETE",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			},
			body: JSON.stringify({
				id
			})
		})
	} catch(error) {
		console.log(error);
	}
}

export function ToDoList() {
	const toDos = useSelector((state: AppState) => state.toDos);
	const datesTasks = useRef<ToDoDateComplete[]>([]);

	useEffect(() => {
    const datesSet = new Set<ToDoDateComplete>(toDos.map(item => item[0]));
    const sortedDates = Array.from(datesSet).sort((a, b) => a - b);
    datesTasks.current = sortedDates;
  }, [toDos]);

	useEffect(() => {
    const unsubscribe = store.subscribe(() => {
      const datesSet = new Set<ToDoDateComplete>(store.getState().toDos.map(item => item[0]));
      const sortedDates = Array.from(datesSet).sort((a, b) => a - b);
      datesTasks.current = sortedDates;
    });
    return unsubscribe;
  }, []);

	useEffect(() => {
    async function getToDoList(): Promise<void> {
			const data: ToDo[] | undefined = await fetchToDoList();
      if (data) {
        store.dispatch({ type: "loadToDos", payload: data } satisfies LoadToDosAction);
      }
    }
    getToDoList();
  }, []);

	function renderToDo():React.ReactNode {
    const newTaskList: React.ReactNode[] = [];
		const storeStateMap = new Map(toDos);

    datesTasks.current.forEach(dateIterator => {
      const tasks = storeStateMap.get(dateIterator);

      if (tasks) {
				const date:Date = new Date(dateIterator);
        newTaskList.push(
					<h3>
						{`до ${date.getDate()} ${getMonth(date.getMonth())} ${date.getFullYear() === new Date().getFullYear() ? '' : `${date.getFullYear()}г.`} ${`${date.getHours()}:${date.getMinutes()}` === '0:0' ? '' : `${getTime(date)}`} `}
					</h3>
				);
				tasks.forEach(todo => {
					newTaskList.push(
						<ToDoComponent funcDelete={deleteToDo} key={todo.id} {...todo} idInList={3}/>
					)
				})
      }
    });
    return newTaskList;
	}

	function deleteToDo(id: ToDoId, taskDateComplete: ToDoDateComplete): void {
		fetchDeleteToDo(id);
		store.dispatch({type: 'deleteToDo', payload: {id, taskDateComplete}} satisfies DeleteToDoAction);
	}

	function getTime(date:Date):string {
		let hours:string = `${date.getHours()}`;
		let minutes:string = `${date.getMinutes()}`;

		if(hours.length === 1) {
			hours = '0' + hours;
		}
		if(minutes.length === 1) {
			minutes = '0' + minutes;
		}

		return `${hours}:${minutes}`
	}
	console.log()
	return (
		<div className={Styles.container}>
		 {store.getState().toDos.length === 0 ? (<div className={Styles.flexContainer}><h2 className={Styles.noToDoH2}>{"Твой ТуДушка пуст :("}</h2></div>) : (renderToDo())}
		</div>
	);
}
