import React, { useEffect, useReducer, useRef} from "react";
import Styles from "./UI/styles.module.css";
import { ToDoComponent } from "../../entities/ToDoComponent/ToDoComponent";
import { LoadToDosAction, DeleteToDoAction, ToDo, ToDoId, store, ToDoDateComplete } from "../../app/store";
import { getMonth } from "../../features/FormChoiceDate/getMonth";

export type ToDoProps = ToDo & {
	funcDelete: (id: number) => void,
	idInList: number
}

async function fetchToDoList():Promise<ToDo[] | undefined> {
	try {
		const token = localStorage.getItem("token");
		const res = await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "GET",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			}
		})
		if (res.ok) {
			const data = await res.json();
			localStorage.setItem('todoListId', data.todoListId);
			return data.toDos as ToDo[];
		}
	} catch(error) {
		console.log(error);
		return [];
	}
}

async function fetchDeleteToDo(id: ToDoId):Promise<void> {
	try {
		const token = localStorage.getItem("token");
		await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "DELETE",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			},
			body: JSON.stringify({
				id
			})
		})
	} catch(error) {
		console.log(error);
	}
}

export function ToDoList() {
	const [taskList, setTaskList] = React.useState<React.ReactElement[]>([]);

	const datesTasks = useRef<ToDoDateComplete[]>([]);

	const [,forceUpdate] = useReducer(x => x + 1, 0);
	useEffect(() => {
		const unsubscribe = store.subscribe(() => {
			forceUpdate();
		});
		return unsubscribe;
	}, [])

	useEffect(() => {
		async function getToDoList():Promise<void> {
			const data: ToDo[] | undefined = await fetchToDoList();
			
			if (data) {
				store.dispatch({type: 'loadToDos', payload: data} satisfies LoadToDosAction);
				const datesSet = new Set<ToDoDateComplete>(store.getState().toDos.keys());
				const sortedDates = Array.from(datesSet).sort((a,b) => a - b);
				datesTasks.current = sortedDates;
			}
		}
		getToDoList();
	}, [])

	useEffect(() => {
    const newTaskList: React.ReactElement[] = [];

    datesTasks.current.forEach(dateIterator => {
      const tasks = store.getState().toDos.get(dateIterator);

      if (tasks) {
				const date:Date = new Date(dateIterator);
        newTaskList.push(
					<h3>
						{`до ${date.getDate()} ${getMonth(date.getMonth())} ${date.getFullYear() === new Date().getFullYear() ? '' : `${date.getFullYear()}г.`} ${`${date.getHours()}:${date.getMinutes()}` === '0:0' ? '' : `${getTime(date)}`} `}
					</h3>
				);
				tasks.forEach(todo => {
					newTaskList.push(
						<ToDoComponent funcDelete={fetchDeleteToDo} key={todo.id} {...todo} idInList={3}/>
					)
				})
      }
    });

    setTaskList(newTaskList);

  }, [datesTasks.current]);

	function deleteToDo(id: ToDoId, TaskDateComplete: ToDoDateComplete): void {
		fetchDeleteToDo(id);
		store.dispatch({type: 'deleteToDo', payload: {id}} satisfies DeleteToDoAction);
	}

	function getTime(date:Date):string {
		let hours:string = `${date.getHours()}`;
		let minutes:string = `${date.getMinutes()}`;

		if(hours.length === 1) {
			hours = '0' + hours;
		}
		if(minutes.length === 1) {
			minutes = '0' + minutes;
		}

		return `${hours}:${minutes}`
	}

	return (
		<div className={Styles.container}>
		 {store.getState().toDos.size === 0 ? (<div className={Styles.flexContainer}><h2 className={Styles.noToDoH2}>{"Твой ТуДушка пуст :("}</h2></div>) : (taskList)}
		</div>
	);
}
