import React, { useContext } from "react";
import Styles from "./UI/styles.module.css";
import { MainContext } from "../../pages/HomePage/HomePage";
import { ToDoList } from "../ToDoList/ToDoList";
import { IMainContext } from "../../app/models/models";
import { store } from "../../app/store";

export function Main() {
	const mainContext = useContext(MainContext) as IMainContext;
	return (
		<main className={Styles.container}>
			{mainContext.isAuth ? 
				<>
					{store.getState().toDos.length !== 0 ? <h2 className={Styles.tasksH2}>Задачи</h2> : ''}
					<ToDoList/> 
				</>
				:
				<></>
			}
		</main>
	);
}
