import React from "react";
import Styles from "./UI/styles.module.css";
import { Button } from "../../shared/Button/Button";
import { IAuthWindow } from "../../app/models/models";

export function AuthWindow(props:IAuthWindow) {

	function handlerKeyDown(e: React.KeyboardEvent<HTMLDivElement>) {
		if(e.key === "Escape") {
			props.functionClick();
		}
	}

	return(
		<div tabIndex={0} onKeyDown={handlerKeyDown} onClick={() => { props.functionClick() }} className = {Styles.container}>
				<form onClick={(e) => e.stopPropagation()}id="formAuth" className = {Styles.form}>
					<svg onClick={() => props.functionClick()} role="button" aria-label="Кнопка закрыть форму авторизации" className={Styles.closeAuthForm} viewBox="0 0 12 12">
						<line x1="1" y1="11" x2="11" y2="1" stroke="#FF4D00" strokeWidth="1"/>
						<line x1="1" y1="1" x2="11" y2="11" stroke="#FF4D00" strokeWidth="1"/>
					</svg>
					<h2 className={Styles.formAuthWindowH2}>Войдите в аккаунт</h2>
					<div className={Styles.inputContainer}>
						<label>Введите email: </label>
						<input className={Styles.formAuthInput}/>
					</div>
					<div className={Styles.inputContainer}>
						<label>Введите пароль: </label>
						<input className={Styles.formAuthInput}/>
					</div>
					<Button type={1}>Войти</Button>
					<div>
						<label htmlFor="buttonNoAccount">Нет аккаунта? </label>
						<a id="buttonNoAccount" href="#">Зарегистрируйтесь</a>
					</div>
				</form>
		</div>
	);
}
