import React, { useEffect, useState } from "react";
import Styles from "./UI/styles.module.css";
import { IToDo } from "../../app/models/models";

async function fetchToDoList() {
	try {
		const token = localStorage.getItem("token");
		const res = await fetch(`http://localhost:6001/api/user/to-do-list`, {
			method: "GET",
			headers: {
				"Content-type": "application/json",
				"authorization": `Bearer ${token}`
			}
		})
		if (res.ok) {
			const data = await res.json();
			return data;
		}
	} catch(error) {
		console.log(error);
	}
}

export function ToDoList() {
	const [toDoList, setToDoList] = useState<IToDo[]>([]);

	useEffect(() => {
		async function getToDoList() {
			const result = await fetchToDoList();
			setToDoList(result);
		}
		getToDoList();
	}, [])

	return (
		<div className={Styles.container}>
		 {toDoList.length === 0 ? <div className={Styles.flexContainer}><h2 className={Styles.noToDoH2}></h2></div> : (
        toDoList.map(item => (
          <div key={item.id}>{item.title}</div>
        ))
      )}
		</div>
	);
}
