import React, { useEffect, useState } from "react";
import Styles from "./UI/styles.module.css";
import { Button } from "../../shared/Button/Button";
import { IAuthWindow, IFetchDataToken } from "../../app/models/models";

async function getTokenAuth(emailInput: string, passwordInput: string) {
	try {
		const res = await fetch('http://localhost:6001/api/user/login', {
			method: "POST",
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				email: emailInput,
				password: passwordInput
			}),
		});
		if (res.ok) {
			const data: IFetchDataToken = await res.json();
			localStorage.setItem("token", data.token)
			return true;
		}
		return "Неверный email или пароль"
	} catch (error) {
		console.log(error);
		return "Ошибка со стороны сервера. Приносим свои извинения за неудобства"
	}
}

async function getTokenReg(emailInput: string, passwordInput: string) {
	try {
		const res = await fetch('http://localhost:6001/api/user/registration', {
			method: "POST",
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({
				email: emailInput,
				password: passwordInput
			}),
		});
		if (res.ok) {
			const data: IFetchDataToken = await res.json();
			localStorage.setItem("token", data.token)
			return true;
		}
		return res
	} catch (error) {
		console.log(error);
		return "Ошибка со стороны сервера. Приносим свои извинения за неудобства"
	}
}

export function AuthWindow(props: IAuthWindow) {
	const [isAuth, setIsAuth] = useState<boolean>(false);
	const [passwordInput, setPasswordInput] = useState<string>('');
	const [emailInput, setEmailInput] = useState<string>('');
	const [errorAuth, setErrorAuth] = useState<string>('');
	const [isReg, setIsReg] = useState<boolean>(props.isRegRef);

	function handlerInputPassword(e: React.ChangeEvent<HTMLInputElement>) {
		setPasswordInput(e.target.value);
	}

	function handlerInputEmail(e: React.ChangeEvent<HTMLInputElement>) {
		setEmailInput(e.target.value);
	}

	function handlerLinkRegOrAuth() {
		isReg === true ? setIsReg(false) : setIsReg(true);
	}

	function handlerSubmit(e: React.FormEvent<HTMLFormElement>) {
		e.preventDefault();
		async function fetchAuth() {
			const result = await getTokenAuth(emailInput, passwordInput);
			if (result === true) {
				setErrorAuth('');
				setIsAuth(true);
			} else if (typeof result === "string") {
				setErrorAuth(result)
			}
		}
		async function fetchReg() {

		}

		isReg === true ? fetchReg() : fetchAuth(); 
	}

	useEffect(() => {
		if (isAuth === true) {
			window.location.reload()
		}
	}, [isAuth]);

	return (
		<div onClick={() => { props.functionClick() }} className={Styles.container}>
			<form onSubmit={handlerSubmit} onClick={(e) => e.stopPropagation()} id="formAuth" className={Styles.form} >
				<svg onClick={() => props.functionClick()} role="button" aria-label="Кнопка закрыть форму авторизации" className={Styles.closeAuthForm} viewBox="0 0 12 12">
					<line x1="1" y1="11" x2="11" y2="1" stroke="#FF4D00" strokeWidth="1" />
					<line x1="1" y1="1" x2="11" y2="11" stroke="#FF4D00" strokeWidth="1" />
				</svg>
				<h2 className={Styles.formAuthWindowH2}>{isReg ? "Регистрация" : "Войдите в аккаунт"}</h2>
				<div className={Styles.inputContainer}>
					<label>Введите email: </label>
					<input placeholder="Ваш email" autoComplete="false" type="email" className={Styles.formAuthInput} value={emailInput} onChange={handlerInputEmail} />
				</div>
				<div className={Styles.inputContainer}>
					<label>Введите пароль: </label>
					<input autoComplete="false" placeholder="Ваш пароль" type="password" className={Styles.formAuthInput} value={passwordInput} onChange={handlerInputPassword} />
				</div>
				<Button type={1}>{isReg ? "Зарегистрироваться" : "Войти"}</Button>
				<div className={Styles.containerNoAccount}>
					<label htmlFor="buttonNoAccount">{isReg ? "Есть учётная запись? " :"Нет аккаунта? "}</label>
					<a className={Styles.linkReg} id="buttonNoAccount" onClick={handlerLinkRegOrAuth} href="#a">{isReg ? "Войдите" : "Зарегистрируйтесь"}</a>
					<p className={Styles.errorText}>{errorAuth}</p>
				</div>
			</form>
		</div>
	);
}
