import React, { useEffect, useState } from "react";
import { Home } from "./pages/Home/Home";
import { IErrorServer } from "./app/models/models";
import { Route, Routes } from "react-router-dom";
import { LoadingPage } from "./pages/LoadingPage/LoadingPage";
import { routes } from "./app/routes/routes"
import { ErrorServer } from "./pages/ErrorServer/ErrorServer";

async function checkAuthorization():Promise<boolean | IErrorServer> {
	try {
		const token = localStorage.getItem('token');
		const controller = new AbortController();
		const timeoutId = setTimeout(():IErrorServer => {
			controller.abort();
			return {isErrorServer: false}
		}, 5000);

		const res = await fetch('http://localhost:6001/api/user/check', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'authorization': 'Bearer ' + token
			},
			signal: controller.signal
		})

		clearTimeout(timeoutId);

		if(!res.ok) {
			console.error('Статус ошибки: ', res.status, '\nСообщение ошибки: ', res.statusText);
			return false;
		}
		const data = await res.json();
		localStorage.setItem('token', data)
		return true;
	} catch(error: any) {
		console.error("Ошибка с сервером: ", error);
		if (error.name === 'AbortError') {
			console.error('Превышено время ожидания ответа от сервера. ', error);
			return {isErrorServer: false};
		}
		return {isErrorServer: false};
	}
}
export default function App() {
	const [isChecked, setIsChecked] = useState<boolean | null | IErrorServer>(null);
	useEffect(() => {
		const authorize = async () => {
			const result = await checkAuthorization();
			setIsChecked(result);
		}
		authorize();
	}, []);
	return (
			<div className="App">
				<Routes element={ <LoadingPage/> }>
					<Route path="*" element={ routes }/>
					{isChecked === null ? <LoadingPage/> : 
					isChecked.hasOwnProperty('isErrorServer') ? <ErrorServer/> : 
					typeof isChecked === 'boolean' ? <Home isChecked={isChecked}/> : <ErrorServer/>}
				</Routes>
			</div>
	);
}
