import { configureStore } from "@reduxjs/toolkit";

export type ToDoId = number;
export type ToDoTitle = string;
export type ToDoDescription = string;
export type ToDoDateComplete = number;

export type ToDo = {
	id: ToDoId,
	dateComplete: ToDoDateComplete,
	title: ToDoTitle,
	description: ToDoDescription,
	isDone: boolean,
	todoListId: number,
	createdAt?: string,
	updatedAt?: string
}

export type ToDoState = Map<number, ToDo[]>;

export type AppState = {
	toDos: ToDoState
}

export type AddToDoAction = {
	type: 'addToDo',
	payload: ToDoState
}

export type DeleteToDoAction = {
	type: 'deleteToDo',
	payload: {
		id: ToDoId
	}
}

type Action = AddToDoAction | DeleteToDoAction;

const initialState: AppState = {
	toDos: new Map()
}


const reducer = (state: AppState = initialState, action: Action) => {
	switch (action.type) {
		case "addToDo":
			return {
				...state,
				toDos: new Map([...state.toDos.entries(), ...action.payload.entries()])
			};
		case "deleteToDo": {
			return {
				...state,
				toDos: [
					...state.toDos.filter(item => item.id !== action.payload.id)
				]
			}
		}
		default: 
			return state;
	}
}

export const store = configureStore({
	reducer: reducer,
})
